#!/bin/bash
# Quick CloudFront setup script - guides through the process
# Usage: ./scripts/setup-cloudfront-quick.sh <stage>

set -e

STAGE="${1:-dev}"

if [ -z "$STAGE" ]; then
  echo "Usage: $0 <stage>"
  echo "Example: $0 dev"
  exit 1
fi

SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
PROJECT_ROOT="$(cd "$SCRIPT_DIR/../.." && pwd)"

echo "🔐 CloudFront Key Pair Setup for $STAGE"
echo "========================================"
echo ""

# Check if key pair files exist
PRIVATE_KEY=$(ls /tmp/cloudfront-private-key-*.pem 2>/dev/null | head -1)
PUBLIC_KEY=$(ls /tmp/cloudfront-public-key-*.pem 2>/dev/null | head -1)

if [ -z "$PRIVATE_KEY" ] || [ -z "$PUBLIC_KEY" ]; then
  echo "❌ Key pair files not found in /tmp/"
  echo "Generating new key pair..."
  cd "$PROJECT_ROOT"
  ./scripts/generate-cloudfront-keypair.sh /tmp
  PRIVATE_KEY=$(ls /tmp/cloudfront-private-key-*.pem 2>/dev/null | head -1)
  PUBLIC_KEY=$(ls /tmp/cloudfront-public-key-*.pem 2>/dev/null | head -1)
fi

echo "✅ Found key pair files:"
echo "   Private: $PRIVATE_KEY"
echo "   Public:  $PUBLIC_KEY"
echo ""

